mapper.insert:
insert into login (
    username,
    password,
    role
) VALUES (
    #{username},
    #{password},
    #{role}
)

INSERT INTO customer (
    cm_phone,
    cm_password
) VALUES (
    #{username},
    #{password}
)

INSERT INTO housekeeper (
    hk_phone,
    hk_password
) VALUES (
    #{username},
    #{password}
)

